content:
table {
  @apply min-w-full divide-y-2 divide-gray-200 text-sm;

  thead {
    @apply text-left;

    tr {
      @apply bg-gray-100;
    }
  }

  th {
    @apply whitespace-nowrap px-4 py-2 font-medium text-gray-900;
  }

  tbody {
    @apply divide-y divide-gray-200;

    tr {
      @apply cursor-pointer hover:bg-gray-100 active:bg-gray-100;

      td {
        @apply px-4 py-2;
      }
    }
  }

}
