package litepay
import (
	"crypto/rand"
	"crypto/rsa"
	"crypto/x509"
	"encoding/pem"
	"errors"
	"strings"
	"testing"

	"github.com/stretchr/testify/assert"
)
func Test_findInSlice(t *testing.T) {
	slice := []string{"apple", "banana", "cherry"}
	cases := []struct {
		slice  []string
		value  string
		result bool
	}{
		{slice, "banana", true},
		{slice, "grape", false},
		{[]string{}, "apple", false},
	}

	for _, tt := range cases {
		found := findInSlice(tt.slice, tt.value)
		assert.Equal(t, tt.result, found)
	}
}
