package queries
func (q *SettingQueries) GroupFieldMap(settings any) map[string]any {
	switch s := settings.(type) {
	case *models.Main:
		return map[string]any{
			"site_name": &s.SiteName,
			"domain":    &s.Domain,
		}
	case *models.Auth:
		return map[string]any{
			"email": &s.Email,
		}
