package litepay
import (
	"errors"
	"fmt"
	"net/http"
	"net/url"
	"strconv"
	"strings"
)
type stripe struct {
	Cfg
	apiToken   string
	successURL string
	cancelURL  string
}
func (c Cfg) Stripe(apiToken string) LitePay {
	c.paymentSystem = STRIPE
	c.api = "https://api.stripe.com"
	c.currency = []string{"EUR", "USD", "GBP", "AUD", "CAD", "JPY", "CNY", "SEK"}
	return &stripe{
		Cfg:        c,
		apiToken:   apiToken,
		successURL: c.successURL,
		cancelURL:  c.cancelURL,
	}
}
