content:
[v-cloak] {
  display: none;
}
body {
  @apply bg-zinc-50 text-sm;
}
h1 {
  @apply text-2xl font-bold text-gray-900 sm:text-3xl;
}
h2 {
  @apply text-xl font-bold text-gray-900 sm:text-2xl;
}
h3 {
  @apply text-xl font-bold text-gray-900;
}
img {
  @apply rounded-lg;
}
header {
  @apply grid grid-cols-[1fr_120px] gap-8 pb-4 relative;

  div {
    @apply absolute right-0;
  }
}
.content-center {
  @apply mx-auto max-w-screen-xl px-4 py-16 sm:px-6 lg:px-8;

  .header {
    @apply mx-auto max-w-lg text-center;

    p {
      @apply mt-4 text-gray-500;
    }
  }
}
.error {
  @apply pl-4 text-sm text-red-500;
}
label {
  @apply relative block rounded border border-gray-200 bg-white pe-10 text-sm shadow-sm focus-within:border-blue-600 focus-within:ring-1 focus-within:ring-blue-600;

  &.none {
    @apply border-none bg-transparent shadow-none;
  }

  .field {
    @apply w-full border-none bg-transparent placeholder-transparent focus:border-transparent focus:outline-none focus:ring-0;
  }

  .title {
    @apply pointer-events-none absolute start-2.5 top-0 -translate-y-1/2 bg-white px-1 text-xs text-gray-700 transition-all;
  }

  .ico {
    @apply absolute inset-y-0 end-0 grid place-content-center pl-4 pr-2;
  }
}
