package queries
func (q *ProductQueries) DeleteImage(ctx context.Context, productID, imageID string) error {
	name, ext, err := q.DeleteProductImageRecord(ctx, productID, imageID)
	if err != nil {
		return err
	}

	return DeleteImageFiles(name, ext)
}
