package litepay
import (
	"encoding/json"
	"errors"
	"fmt"
	"net/http"
	"strconv"
	"strings"
)
type paypal struct {
	Cfg
	clientID  string
	secretKey string
}
func (c Cfg) Paypal(clientID, secretKey string) LitePay {
	c.paymentSystem = PAYPAL
	c.api = "https://api.sandbox.paypal.com" // https://api.paypal.com
	c.currency = []string{"EUR", "USD", "GBP", "AUD", "CAD", "JPY", "CNY", "SEK"}
	return &paypal{
		Cfg:       c,
		clientID:  clientID,
		secretKey: secretKey,
	}
}
